FabFilterToggle=new Class({initialize:function(d){var e=document.id("list_"+d);var b=document.id("listform_"+d);Fabrik.addEvent("fabrik.list.update",function(c){if(c.id===d){e.getElements(".fabrik___heading span.filter").hide()}return true});e.getElements("span.heading").each(function(c){var g=c.getNext();if(g){c.addClass("filtertitle");c.setStyle("cursor","pointer");if(i=g.getElement("input")){i.set("placeholder",c.get("text"))}g.hide()}});e.addEvent("click:relay(span.heading)",function(k){var g=k.target.getNext();if(g){g.toggle();var c=b.getElement(".fabrikFilterContainer");var j=e.getOffsetParent()?e.getOffsetParent():document.body;var h=g.getPosition(j);c.setPosition({x:h.x-5,y:h.y+g.getSize().y});if(g.getStyle("display")==="none"){c.hide()}else{c.show()}}});var f=b.getElement(".clearFilters");if(typeOf(f)!=="null"){f.addEvent("click",function(){b.getElement(".fabrikFilterContainer").hide();b.getElements(".fabrik___heading .filter").hide()})}var a=b.getElement(".fabrik_filter_submit");if(typeOf(a)!=="null"){a.addEvent("click",function(){b.getElement(".fabrikFilterContainer").hide();b.getElements(".fabrik___heading .filter").hide()})}}});